void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    // Convert to polar
    vec2 cuv = abs(uv - 0.5);
    float a = atan(cuv.x, cuv.y);
    float r = length(cuv);

    // FFT
    float fft = texture(iChannel0, vec2(r, 0.25)).x;

    vec4 base = vec4(uv,0.5+0.5*sin(iTime),1.0);
    float boost = 0.5;
	fragColor = base * (sin(r * 64.0 * 3.1415 - iTime ) * fft);
}
