// From: https://www.shadertoy.com/view/4lX3WN
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2  p = 2.*fragCoord.xy/iResolution.xy - 1.;
    fragColor = vec4( fract( 12.*atan(p.y,p.x) + .5*iTime - 1440.*log(length(p))) > .5 );
}
