void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy, V = floor(U-R/2.);
    if (V.x*V.y==0.) { O=vec4(1,0,0,0); return; }

    U /= R;

	float a  = sqrt(-U.x),   // NaN
        zero = min(0.,U.x);  // zero

    O = vec4( U.y > .5 
                  ? U.x < .5 ?  (a+0.)/a  : ( a+zero ) / a 
                  : U.x < .5 ?  0.*a + 1. :   zero * a + 1. 
            );
}
