void mainImage( out vec4 f, in vec2 w )
{
    float t = iTime*1.4;
	vec2 uv = w / iResolution.xy;

    for(int i = 0; i<4; i++)
    {
        uv+=-.5;
        vec2 c = vec2(-.2+uv.x,-.1+uv.y-sin(t));
        c*=cos(t-uv);
        uv=abs(uv);
        float m=uv.x*uv.x+uv.y*uv.y;
        uv-=((uv)/m+c);
    }

	f = vec4((uv*.5),uv.y,1.0);
}
