struct dc {
	float d;
    vec3 c;
};

float circle(vec2 p)
{
	return length(p)-1.0;
}

dc smoothmin(float a, float b, float c, float d, float e, vec3 ca, vec3 cb, vec3 cc, vec3 cd, vec3 ce)
{
    float k = 0.5;
	float dis = -log(exp(-a*k)+exp(-b*k)+exp(-c*k)+exp(-d*k)+exp(-e*k))/k;
    vec3 col = (ca*(1.0/a)+cb*(1.0/b)+cc*(1.0/c)+cd*(1.0/d)+ce*(1.0/d))/(1.0/a+1.0/b+1.0/c+1.0/d+1.0/e);
    return dc(dis, col);
}

dc scene(vec2 p)
{
    vec2 center = p * 6.0 - vec2(3.0, 3.0);

    vec2 p1 = center + 2.0 * vec2(cos(iTime*2.1),sin(iTime*1.3));
    vec2 p2 = center + 2.0 * vec2(cos(iTime*2.9),sin(iTime*1.7));
    vec2 p3 = center + 2.0 * vec2(cos(iTime*1.1),sin(iTime*0.7));
    vec2 p4 = center + 2.0 * vec2(cos(iTime*1.9),sin(iTime*2.3));
    vec2 p5 = center + 2.0 * vec2(cos(iTime*1.3),sin(iTime*1.1));

    vec3 col1 = vec3(1.0,0.0,0.0)/5.0;
    vec3 col2 = vec3(0.0,1.0,0.0)/5.0;
    vec3 col3 = vec3(0.0,0.0,1.0)/5.0;
    vec3 col4 = vec3(0.2,0.4,0.4)/5.0;
    vec3 col5 = vec3(0.4,0.4,0.2)/5.0;

	return smoothmin(circle(p1), circle(p2), circle(p3), circle(p4), circle(p5), col1, col2, col3, col4, col5);  
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    dc res = scene(uv);
    float d = smoothstep(0.0, 0.1, res.d);

    vec3 col = res.c * res.d * (1.0-d);
    col += texture(iChannel0, uv + col.xy/50.0).rgb;
    col += texture(iChannel0, uv * 2.0 + col.xy/50.0).rgb;
    col /= 2.0;

    fragColor = vec4(col, 0.0);
}
