void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 screenPos = fragCoord.xy / iResolution.xy; // position in the screen
    vec2 mouse = iMouse.xy / iResolution.xy; // mouse position relative to the screen
    vec4 channel0 = texture(iChannel0, screenPos); // iChannel0
    vec4 channel1 = texture(iChannel1, screenPos); // iChannel1
    vec4 channel2 = texture(iChannel2, screenPos); // iChannel2
    vec4 channel3 = texture(iChannel3, screenPos); // iChannel3

//    vec4 color = vec4(.0,1.0,.0,1.0); // final color, initial value is white
    vec2 displaceFactor = vec2(sin(screenPos.y * 16.0) / screenPos.y * .015 , 0.0);
    vec4 grey = vec4(.5, .5, .5, 1.0);

	//vec4 color = channel0;
    float r = texture(iChannel0, screenPos - vec2((0.5 + sin(channel2.y * 5.0)/2.0) * 0.03, 0.0) + displaceFactor).r;
    float b = texture(iChannel0, screenPos + vec2((0.5 + sin(channel2.y * 5.0)/2.0) * 0.03, 0.0) + displaceFactor).b;

    float g = texture(iChannel0, screenPos + displaceFactor).g;

    //color = mix(color, r, .5);
    vec4 colorDistort = vec4(r, g, b, 1.0);

    vec4 color = mix(grey, colorDistort, 0.8);
	fragColor = color;
}
