void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // see https://github.com/mattdesl/lwjgl-basics/wiki/ShaderLesson3
	vec2 xy = fragCoord.xy / iResolution.xy;
    xy.y = 1.0 - xy.y; // flip image y axis to match canvas
    vec4 texColour = texture(iChannel0, xy); // get pixel from input img

    // all the magic numbers
    float alpha = abs(sin(iTime));
    float luminance = dot(texColour.rgb, vec3(0.299, 0.587, 0.114));

    texColour.rgb = mix(texColour.rgb, vec3(luminance), alpha);
    fragColor = texColour;
}
