#define PI 3.1415926535897932384
#define THETA 6.2831853071795864769

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime;
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 suv = vec2(((uv.x-0.5)*(iResolution.x / iResolution.y))+0.5,uv.y); //Stretched UV
    float drawing = 1.-((length(suv-0.5)-0.18)*600.); //Draw circle
    drawing = clamp(drawing,0.,1.); //Clamping
    float sharpen = length(suv-0.5)*38.; //Do not make the center too sharp
    drawing += clamp(sin((((atan(suv.x-0.5,suv.y-0.5)+PI)/THETA)*(PI*24.))+time)*sharpen,0.,1.); //Draw lines around the circle
    drawing = clamp(drawing,0.,1.); //Clamping
    vec3 final_drawing = vec3(1.,1.-drawing,1.-drawing);
	fragColor = vec4(final_drawing*(1.-length(uv-0.5)/3.),1.0);
}
