void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float quadnumber = 16.0;
    float quadsize = 1.0/quadnumber;
    float presentquad = 0.0;
    float sinfactor = 2.0;
    bool isnextquad = false;
    bool ispresentquad = false;
    presentquad =  (quadnumber - 1.0) - (quadnumber - 1.0) * abs(sin(sinfactor*iTime));
    if( abs(sin(sinfactor*iTime)) < abs(sin(sinfactor*iTime - 0.01)))
           presentquad = (quadnumber - 1.0) * abs(sin(sinfactor*iTime));
    isnextquad = abs(uv.x - 0.5) < (quadsize*(presentquad + 1.0)) && abs(uv.y - 0.5) < (quadsize*(presentquad + 1.0)); 
    ispresentquad = abs(uv.x - 0.5) < (quadsize*(presentquad + 0.0)) && abs(uv.y - 0.5) < (quadsize*(presentquad + 0.0));
    //
    //presentquad =  (mod(quadnumber , iTime));

    if(isnextquad && !ispresentquad)
    {
       fragColor = vec4(1.0 - presentquad/quadnumber , presentquad/quadnumber , 1.0 , 1.0);
    }
    else fragColor =vec4(0.0);

    /*if( abs(uv.x - 0.5) < (0.125*(presentquad + 1.0)) && abs(uv.y - 0.5) < (0.125*(presentquad + 1.0)))
    {
    	fragColor = vec4(0.0);
    }
    else
        fragColor = vec4(1.0);*/




    //if(uv.x > 0.5 && uv.y > 0.5) fragColor = vec4(1.0);
	//else fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
}
