void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    // the sound texture is 512x2
    int tx = int(uv.x*512.0);

    // first row is frequency data (48Khz/4 in 512 texels, meaning 23 Hz per texel)
	float fft  = texelFetch( iChannel0, ivec2(tx,0), 0 ).x; 

    // second row is the sound wave, one texel is one mono sample
    float wave = texelFetch( iChannel0, ivec2(tx,1), 0 ).x;



    vec4 audio = texture(iChannel0, uv);

    // a. The DISTANCE from the pixel to the center
    float dfc = distance(uv,vec2(0.5));

    float le = 0.1 * 1.+(abs(wave)/5.);
    float he = 0.5 * 1.+(abs(wave)/5.);

    float pct = smoothstep(le, he, dfc); //smoothstep(hedge, ledge, dfc);

    float radius = 0.5;
    vec4 red = vec4(1.0, 0.0, 0.0, 1.0);


    vec3 col = vec3(pct*sin(fft), 1.0-pct, pct*cos(fft));
	fragColor = vec4(col, 1.0);
}
