// +++ Hash from https://www.shadertoy.com/view/MtlczH
#define SEED 1.123456789
#define HASHM mat3(40.15384,31.973157,31.179219,10.72341,13.123009,41.441023,-311.61923,10.41234,178.127121)
float hash(vec2 p)
{
    vec3 p3 = fract(vec3(p.x, p.y, (p.x + p.y + SEED * 1e-7)) * HASHM);
    p3 += dot(p3, p3.yzx + 41.19);
    return fract((p3.x + p3.y) * p3.z);
}
// +++

vec2 hash2(vec2 p)
{
    return vec2(hash(p), hash(p + vec2(0.942)));
}

#define NUM_PTS 32
#define EPS 0.001
#define BIG_NUMBER 999.

float dPoint(vec2 p)
{
    return length(p);
}

vec2 ptCoord(vec2 seed)
{
    vec2 a = hash2(seed + 0.531);
    vec2 b = hash2(seed + 0.273);
    return hash2(seed) - 0.5 + vec2(a.x*cos(iTime*b.x), a.y*sin(iTime*b.y));
}

float voronoi(vec2 p)
{    
    vec2 f = vec2(BIG_NUMBER);
    for (int i = 0; i < NUM_PTS; ++i) {
        vec2 seed = vec2(float(i), 0.);
        float d = dPoint(p - ptCoord(seed));
        f = mix(mix(f, vec2(f.x, d), step(d, f.y)), vec2(d, f.x), step(d, f.x));
    }
    return f.y - f.x;
}

vec2 distort(vec2 p)
{
    return p + voronoi(p)*16.;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = ( fragCoord - .5* iResolution.xy ) / iResolution.x;
    fragColor = texture(iChannel0, distort(uv));
}
