// variant from https://www.shadertoy.com/view/MlXcWn


void mainImage( out vec4 O, vec2 U )
{
    float N = 3.,              // number of axis
          S = 8.,              // scale
          a = 3.14/N, 
          h = iResolution.y;
    U /= h; U -= .5; U *= S;
    O -= O;

    for (float i=0.; i<N; i++)
        O = max( O,    smoothstep(S/h, 0., abs(fract(U.x)-.5)-.1)              // strip
                   * ( .7 + .3* sin(6.28* ( U.y*1.73/2. + .5*floor(U.x) ))) ), // waves
        U *= mat2(cos(a),-sin(a),sin(a),cos(a));


}
