//variables
float rows = 5.0;
float cols = 3.0;
vec4 white = vec4(1.0,1.0,1.0,1.0);
vec4 black = vec4(0.0,0.0,0.0,1.0);
float AspectRatio;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 texture1 = texture(iChannel0, uv);
    vec4 texture2 = texture(iChannel1, uv);

    //Fixes the aspect ratio
   	AspectRatio= (iResolution.y/iResolution.x) * (rows/cols);
	uv.y *= AspectRatio;

    //Makes the Checker Board
    if(mod(uv.x*rows, 2.0)> 1.0 && mod(uv.y*cols, 2.0)> 1.0)
    	fragColor = texture1;
    else if(mod(uv.x*rows, 2.0) < 1.0 && mod(uv.y*cols, 2.0) < 1.0)
    	fragColor = texture1;
    else
        fragColor = texture2;
}
