void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float radius = iResolution.y / 2.5;
    vec2 center_of_circle = vec2( iResolution.xy / vec2(2.0, 2.0) );

    float redVal = floor( (iResolution.x - fragCoord.x) / iResolution.x + 0.5 );
    float greenVal = ceil(max(radius - distance(fragCoord, center_of_circle), 0.0));
    float blueVal = cos(floor( (iResolution.x - fragCoord.x) / iResolution.x + 0.5 ) * 90.0);

    fragColor = vec4(
        redVal - greenVal,
        greenVal,
        blueVal - greenVal,
        1.0);
}
