vec3 camPos = vec3(0, 0, 1000);

vec3 normal(vec3 p) {
    if (p.z >= 150. && round(distance(p, vec3(sin(iTime) * 200., 0, 0))) == 200.) {
        return normalize(-p);
    }

    return vec3(0, 0, 0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec3 screenPos = vec3(fragCoord.xy - iResolution.xy * .5, 250);
    vec3 ray = normalize(screenPos - camPos);

    vec3 p = screenPos;

    for (int i = 0; i <= 100; i++) {
        vec3 n = normal(p);

        if (n != vec3(0, 0, 0)) {
            vec3 p0 = p;

            for (int j = 0; j <= 250; j++) {
                if (round(p0.z) == 250.) {
                    vec2 uv = (p0.xy - iResolution.xy * .5) / iResolution.xy;
                    fragColor = vec4(texture(iChannel0, uv).xyz, 0.);

                    return;
                }

                p0 -= n;
            }

            return;
        }

        p += ray;
    }

    vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = vec4(texture(iChannel0, uv).xyz, 0.);
}
