//Input the number of rows/columns that checkboard has 
float numberOfRows = 6.0; 
float numberOfCols = 8.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 bg = texture(iChannel0, uv);
	vec4 picture = texture(iChannel1,uv);
    float aspect = iResolution.y/iResolution.x;

    float total = floor(uv.x * numberOfCols) + floor(uv.y * numberOfRows);

    if(mod(total, 2.0) == 0.0)
        fragColor = bg;
    else
        fragColor = (picture + bg) / 2.0;

}
