vec3 Geoffrey(float t)
{
    vec3 r = t * 2.1 - vec3(1.8, 1.14, 0.3);
    return 1.0 - r * r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 ori=uv*2.-1.;
    vec3 res=vec3(0.);
    for(int i=0 ; i<5 ; ++i)
    {
    	uv=abs(uv)/dot(uv,uv)*1.6-1.6;
    	res+=0.6*exp(-20. * abs(dot(uv,ori)))*Geoffrey(float(i)/4.);        
    }
	fragColor.xyz =res;
}
