float maxComponent(vec3 v) { return max(max(v.x, v.y), v.z); }
vec4 rgb2cmyk(vec3 rgb)  { 
    float kp = maxComponent(rgb);
    return vec4((kp - rgb) / kp, 1.0 - kp); }
vec3 cmyk2rgb(vec4 cmyk) { return (1.0 - cmyk.rgb) * (1.0 - cmyk.w); }

vec3 ps_rgb2bw(vec3 rgb) {
    const vec3 _RGBInfluence = vec3( 0.22, -2.00,  -0.70);
    const vec3 _CMYInfluence = vec3(-2.00,  1.80,  0.60);
    const vec3 _CMYMultiplier = vec3(-1, 1, 1);

    vec4  cmyk = rgb2cmyk(rgb);
    vec3  rgb2 = rgb * _RGBInfluence,
            cmyRgb = cmyk2rgb(vec4(cmyk.rgb * _CMYInfluence * _CMYMultiplier, cmyk.w));

    return vec3(dot(rgb2 + cmyRgb, vec3(1.7)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    //vec3 rgb = vec3(uv, 0.5 + 0.5 * sin(iTime));
    vec3 rgb = texture(iChannel0, uv).rgb;

    if(iMouse.x > fragCoord.x) {
        fragColor = vec4(rgb, 0);
    }
    else {
		fragColor = vec4(ps_rgb2bw(rgb), 0);
    }
}
