const float pi = 3.14159;
const float count = 10.0;
const float oneOverCount = 1.0 / (count-1.0);

float when_gt(float x, float y){
	return max(sign(x-y),0.0);
}

float when_lt(float x, float y){
	return max(sign(y-x),0.0);
}

float when_hlt(float x, float n, float m){
	return max(sign(x-m) * sign(n-x), 0.0);
}

float bump(float x, float o){
    return (cos(2.0*pi*(x-o)+pi)*0.5+0.5) * when_gt(x,o) * when_lt(x, o+1.0);
}

float fade(float x, float o){
	return (cos( (x-o)*pi )*0.5+0.5) * when_hlt(x, o, o+1.0) + when_lt(x, o);
}

bool isLine(float y, float line){
    return y == oneOverCount * line;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 res = iResolution.xy;
    vec2 coord = fragCoord.xy;
	vec2 uv = coord / res;

    vec3 c = texture(iChannel0, uv).xyz;

	vec3 color = vec3(0);

    float x = floor(uv.x * count)*oneOverCount;
    float y = floor(uv.y * count)*oneOverCount;
    float id = x + y * count;

    vec3 backColor = vec3(x,y,0.5);
    //vec3 backColor = vec3(x,y,0) * 0.3 + vec3(1.0-x,1.0-y,0) * 0.7;
    color = backColor * 0.3;

// MOUSE //////
    float nMouseX = iMouse.x / res.x;
    float nMouseY = iMouse.y / res.y;
    float nMouseZ = iMouse.z / res.x;

// ANIMATION //////

//offset
    float offset = c.x;

//Alpha
    //float alpha = bump(iTime, offset);
    float alpha = fade(iTime, offset);
    //float alpha = bump(mod(iTime, 5.0), id * 0.1);

//final color
    color += backColor * (alpha * 0.7);

//mouse press color

    float mx = floor(nMouseX * count)*oneOverCount;
    float my = floor(nMouseY * count)*oneOverCount;

    float isMouseDown = ceil(nMouseZ);

    if(x == mx && y == my && isMouseDown == 1.0){
        color = backColor;
    }


// DEBUG //////////
    if(false){
	    bool isLeftHalf = uv.x < 0.5;
	    bool isRightHalf = !isLeftHalf;

	    if( isLine(y,0.0) ){
	        if( isLeftHalf ){
	            color = vec3(iMouse.x / res.x, 0,0);
	        }else{
	            color = vec3(iMouse.z / res.x, 0,0);
	        }
	    }
	    if(isLine(y,1.0)){
	        color = vec3(nMouseX * isMouseDown, 0,0);
	    }
    }

	fragColor = vec4(color, 1);
}

const float count = 10.0;
const float oneOverCount = 1.0 / (count-1.0);

bool isLine(float y, float line){
    return y == oneOverCount * line;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 res = iResolution.xy;
    vec2 coord = fragCoord.xy;
	vec2 uv = coord / res;

    float x = floor(uv.x * count)*oneOverCount;
    float y = floor(uv.y * count)*oneOverCount;
    float id = x + y * count;


    vec3 color = texture(iChannel0, uv).xyz;


// MOUSE //////
    float nMouseX = iMouse.x / res.x;
    float nMouseY = iMouse.y / res.y;
    float nMouseZ = iMouse.z / res.x;

    float mx = floor(nMouseX * count)*oneOverCount;
    float my = floor(nMouseY * count)*oneOverCount;

    bool isMouseDown = nMouseZ > 0.0;

    if(x == mx && y == my && isMouseDown){
        color = vec3(iTime);
    }

	fragColor = vec4(color, 1);
}
