#define tau 6.28318
#define speed 0.05

// source: https://iquilezles.org/articles/palettes
// cosine based palette, 4 vec3 params
vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( tau*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = -1. + 2. * fragCoord.xy / iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    uv.x = sin(uv.x)*cos(uv.x);

    // mirror to remove mipmap artifact
    uv.x = abs(uv.x);

    float r = length( uv );

    uv = vec2(0., 1.0/r + speed*iTime + (iMouse.y / iResolution.y));

    float mask = dot(vec3(0.2126,.7152,0.0722),texture(iChannel0, uv).rgb);

    float t = mod(r*.85+(iTime*speed)-0.2,1.);
    vec3 tint = palette(t,
                   vec3(0.5, 0.5, 0.5),
                   vec3(0.5, 0.5, 0.5),
                   vec3(1.0, 1.0, 0.5),
                   vec3(0.8, 0.9, 0.3));

    vec3 col = 3.0*r*mask*tint;

	fragColor = vec4(col, 1.);
}
