// rings
float circ(vec2 p,float radius){
	return length(p )-radius>0.?0.:1.  ;
}



// pattern that repeats after just few rounds
//  vec3 speeds=vec3(2.,2.,2.);

// longer prime number pattern, repeats after some more frames ....  31*53*67=110081 frames
// vec3 speeds=vec3(1.31,1.53,1.67);


// very long prime number pattern, repeats after some more frames ....  33637*68023*94321=some more frames
vec3 speeds=vec3(1	, 1	,1	);
float speed=2.;
float moveradius=0.35;
float radius=0.1;
float thickness=.07;  


vec3 color1=vec3(1.,0.,0.);
vec3 color2=vec3(0.,1.,1.);
vec3 color3=vec3(.5,1.,0.);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv-=0.5; 
    uv.x*=iResolution.x/iResolution.y;
    vec2 speedsinteractive=(iMouse.xy/iResolution.xy)*2.0+1.;
    vec2 p1=vec2(sin(iTime*speeds.x*speed),cos(iTime*speeds.x*speed))*(moveradius)-vec2(moveradius+radius/2.,0.);
    vec2 p2=vec2(sin(iTime*speeds.y*speed*speedsinteractive.x),cos(iTime*speeds.y*speed*speedsinteractive.x))*(moveradius);
    vec2 p3=vec2(sin(iTime*speeds.z*speed*speedsinteractive.y),cos(iTime*speeds.z*speed*speedsinteractive.y))*(moveradius)+vec2(moveradius+radius/2.,0.);

	vec3 col = vec3( circ(uv+p1,radius/2.))*color1;
	col +=circ(uv+p2,radius/2.)*color2;
	 col += circ(uv+p3,radius/2.)*color3;
    fragColor=vec4(col,1.);
}
