#define animSpeed 5.0
#define PI 3.14159265
#define HALFPI PI/2.0
float S(float v) {
    return smoothstep(1., 0., abs(v)/fwidth(v));
}
float Smodpi(float v) {
    return smoothstep(1., 0., abs(mod(v, PI)-HALFPI)/fwidth(v));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (-iResolution.xy + 2. * fragCoord.xy) / iResolution.y;
    float d2 = dot(uv, uv);
    float col = 1.;
    float a = atan(uv.y/uv.x);
    float iT = iTime/animSpeed;
    if (d2 <= 0.563) {
        float d = sqrt(d2);
        float outer = 0.015*sin(12.*(a - iT)) + 0.5 + 0.05 * sin(iT * 10.);
        float inner = 0.015*sin(12.*(a - iT)) + 0.4 + 0.05 * sin(iT * 10.); 	
        col -= S(outer - d);
        col -= S(inner - d);
        if (d <= inner) {
            col -= Smodpi((iT-a)*4.);
        }
    }
    fragColor = vec4(col);
}
