// Matrix-based RGB from/to YCoCg color space conversion

// Copyright (C) 2014-2018 by Benjamin 'BeRo' Rosseaux

// Because the german law knows no public domain in the usual sense,
// this code is licensed under the CC0 license 

// http://creativecommons.org/publicdomain/zero/1.0/

// It's e.g. useful for Chrominance Compression at spherical-harmonics-based and spherical-gaussians-based
// Global Illumination solutions, see http://jcgt.org/published/0003/04/06/paper.pdf and
// http://jcgt.org/published/0005/04/02/paper.pdf

const mat3 RGBToYCoCgMatrix = mat3(0.25, 0.5, -0.25, 0.5, 0.0, 0.5, 0.25, -0.5, -0.25);

const mat3 YCoCgToRGBMatrix = mat3(1.0, 1.0, 1.0, 1.0, 0.0, -1.0, -1.0, 1.0, -1.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 c = texture(iChannel0, uv).xyz;
    vec3 YCoCg = RGBToYCoCgMatrix * c;
    vec3 RGB = YCoCgToRGBMatrix * YCoCg;
	fragColor = vec4(mix(mix(c, YCoCg, step(0.3333, uv.x) * ((sin(iTime * 6.28318 * 0.25) * 0.5) + 0.5)), RGB, step(0.6666, uv.x)), 1.0);
}
