//based on https://www.shadertoy.com/view/lsffDf
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = fragCoord.xy / iResolution.xy;
    uv-=0.5;


    //float aspect=iResolution.x/iResolution.y;
    //uv.x*=aspect;

    //uv*=uv;
    //float q=uv.y*uv.y*uv.y+uv.x*uv.x*uv.x;
    //float q=(uv.x*uv.x+uv.y*uv.y*uv.y)*uv.y;
    //float q=sin(uv.x*uv.x)+cos(uv.y*uv.y*uv.y);
    float q=dot(uv,uv);
    //float q=abs(uv.y)+abs(uv.x);



    vec3 c = cos(((iTime*-0.35 +0.0)+q)*20.0 + vec3(0.3,0.7,0.9));

    //vec3 c= cos(((iTime*-0.35 +0.0)*vec3(1.0,1.0,1.0)+q)*(vec3(0.0,0.1,0.2)+20.0));


    c=c*0.5+0.5;
    //c=c*0.7+0.5; //sharper

    //c=abs(c);
    //c=normalize(c);

    //c*=1.5; //sharper

    fragColor = vec4(c,1.0);
}

