void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - 0.5;
    vec2 perm = vec2(0.0,0.0);
    perm.x += uv.x*abs(cos(iTime)/2.0)+0.5;
    perm.y += 0.001*uv.y*abs(sin(iTime)/2.0)+0.5;
	fragColor = vec4(texture(iChannel0,mix(perm,vec2(uv.y,uv.y*0.1), 1.0+sin(iTime))));
    fragColor.y = pow(fragColor.y,2.0);
    fragColor.z= fragColor.y*fragColor.y - 0.25;
    vec4 warrior = vec4(texture(iChannel1,uv+0.5, 1.0));
    float distToSpot=distance(uv,vec2(-0.5,-0.5));
    float spotRadius = 1.0;
    if(distToSpot<spotRadius){
        distToSpot *= 1.0/spotRadius;
        fragColor += vec4(spotRadius-distToSpot,spotRadius-distToSpot,spotRadius-distToSpot,1.0);
    }

    if(warrior.y < 0.5 || length(warrior) > 1.3 )
    {
        // warrior.z = warrior.x = warrior.y;
        fragColor = warrior;
    }

}
