float y(float t, float yv) {
    return -0.01 + yv*t + -1.0*t*t*0.5;
}

float x(float t, float xv) {
    return 0.5 + xv*t;
}

vec2 particle(float t, float xv, float yv) {
    t = mod(t, 1.94152);
    vec2 p = vec2(x(t, xv)-0.2*xv, y(t, yv));
    return p;
}

float hash(float v) {
    return fract(sin(v*27179.18917));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = vec2(1.0, iResolution.y / iResolution.x) * fragCoord.xy / iResolution.xy;
    float r = 0.003;
    float pxSize = 1.0/iResolution.x;
    fragColor = vec4(0.3*(1.0-abs((uv.x-0.5)))*pow(1.0-uv.y,8.0), 0.4*uv.y, 0.6*uv.y, 1.0) + hash(iTime+fragCoord.x/fragCoord.y)/255.0;
    float v = 0.0;
    for (float j=0.0; j<4.0; j++) {
    	for (float i=0.0; i<100.0; i++) {
			v += mix(1.0, 0.0, smoothstep(r, r+pxSize, length(particle(iTime+i+(j+hash(i*j+iTime+fragCoord.x/fragCoord.y))*(1.0/(2.0*60.0)), sin(i)*0.2, 0.9+0.05*cos(i*5.1))-uv)) );
    	}
    }
    v /= 4.0;
    fragColor.r += v;
    fragColor.g += v * 2.5*uv.y*uv.y;
}
