void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord - 0.5*iResolution.xy) / iResolution.y;

    uv += vec2(0., sin(iTime*0.125)) * 0.33;
    float angle = atan(uv.y, uv.x)+cos(iTime*0.25)*1.;

    float d = length(uv);
    float z = 0.1 / d + iTime*.15;

    vec3 ray = normalize(vec3(uv, 1.));
    vec3 normal = vec3(normalize(uv), 0.);
    vec3 light = normalize(vec3(0.25, -1, 0.));

    float diffuse = max(dot(normal, -light), 0.);

    vec4 tunnel = texture(iChannel0, vec2(angle/6.283, z)); 
    vec4 envmap = texture(iChannel1, ray);

    fragColor = mix(envmap, tunnel * (0.75+diffuse), smoothstep(0.25, 0.5, length(tunnel.rgb)));
}
