vec2 wave(in vec2 uv, int p, float f, float a, float o)
{
    if (a < 0.001)
        a = 0.00000001;
    a = 1.0 / a;
    uv.x = mod((uv.x * float(p) + iTime * f) * 360.0,360.0);
	uv.y = ((uv.y - o) * 2.0 * a - a);
    return uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 2.0 * fragCoord.xy / iResolution.xy - 1.0;
    uv.x = uv.x * iResolution.x / iResolution.y;
	vec2 xy = uv;
    float s = sin(iTime * 50.0 + uv.x + (-sin((uv.y + 1.0) * 90.0 * 3.14 / 180.0) + 1.0) / 2.0);
    vec3 col = vec3(0);
    //col = vec3(0, 0, s);
    col = clamp(col, 0.0, 1.0);
    if (uv.x < 0.0)
        uv.x = uv.x * cos((uv.y + iTime / 10000.0) * 2.0 * 5000.0);
    uv.y += cos(iTime) * 0.5;
    float l = length(uv);
    if (l <= 0.5)
    {
        s = (sin((l + 0.5) * 720.0 * 3.14 / 180.0) + 1.0) / 2.0;
        float c = (cos(l * 360.0 * 3.14 / 180.0) + 1.0) / 2.0;
        col = vec3(1.0 * s, s * (cos(uv.y * 2.0) + cos(uv.x * 2.0) / 2.0 - 1.0), c);
    }
    fragColor = vec4(col,1.0);
}
