void mainImage( out vec4 col, in vec2 p ) {
	p /= iResolution.xy;

    float r = 0.0;
    float pass = 0.0;

    for (int i = 0; i < 50; i++) {
        r += smoothstep(0.3, 0.4, texture(iChannel0, vec2(p.x + cos(pass)*0.01, p.y + sin(pass)*0.05)).g);
        pass += 1.0;
    }
    r /= pass;

    col.rgb = vec3(0.0) + r;
}
