void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // 画面上の座標を求める.
    // iTimeは、現在の時間経過に関する値で時間経過で変化するようなアニメーション表現に利用する.
    // この例では、iTimeをsin関数に与えることで周期的な色の変化を行なっている。
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 color = vec4(uv, 0.5 + 0.5 * sin(iTime), 1.0);

    // 画面上の座標値を-1.0から
    vec2 pos = uv * 2.0 - 1.0;

    color *= abs(1.0 / (sin(pos.x + cos(pos.y + iTime) * 0.5) * sin(iTime * .3) * 70.0));
    color *= abs(1.0 / (sin(pos.y + cos(pos.x + iTime) * 0.2) * sin(iTime * .3) * 70.0));

    fragColor = color;
}
