const float M_PI = acos( -1.0 );

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float phase = 8.0 * M_PI + 4.0 * sin( iTime * 9.0  + 7.0 * uv.x + 3.0 * uv.y );
    uv = abs( uv + vec2( -0.5, -0.5 ) );
    float r = cos( phase * uv.x * M_PI );
    float t = cos( phase * uv.y * M_PI );
    float p = clamp( r, 0.2, 0.7 );
    float q = clamp( t, 0.2, 0.7 );
    float r_color = max( p, q );
    float g_color =  step( 0.5, p );
    float b_color =  step( 0.5, q );

    fragColor = vec4( r_color, g_color, b_color, 1.0 );
}
