float sdfCircle(vec2 point,float radius){

    return length(point)-radius;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv*2.-1.;
    uv *= 100.;
    uv.y /= iResolution.x/iResolution.y;

    float smoothDelta = 2.;

    vec3 fontColor = vec3(0.,1.,0.);

    vec3 backColor = vec3(1.);

    float dis = sdfCircle(uv,30.);

    float a = 1.-smoothstep(-smoothDelta,smoothDelta,dis);

    fragColor.rgb = mix(backColor,fontColor,a);




}
