  #define FastNoise(posX) (  textureLod(iChannel1, (posX+0.5)/iResolution.xy, 0.0).r)
  #define CAMZOOM  13.9
  #define readAlpha(memPos) (  textureLod(iChannel2, memPos, 0.0).a)
  #define read(memPos) (  textureLod(iChannel0, (memPos+0.5)/iResolution.xy, 0.0).a)
  #define readRGB(memPos) (  textureLod(iChannel0, (memPos+0.5)/iResolution.xy, 0.0).rgb)
  #define STARTHEIGHT 10.
#define PI acos(-1.)
mat3 cameraMatrix;

vec3 sunPos;
const vec3 eps = vec3(0.02, 0.0, 0.0);

mat3 setCamera(  vec3 ro, vec3 ta, float cr )
{
  vec3 cw = normalize(ta-ro);
  vec3 cp = vec3(sin(cr), cos(cr), 0.0);
  vec3 cu = normalize( cross(cw, cp) );
  vec3 cv = normalize( cross(cu, cw) );
  return mat3( cu, cv, cw );  
}

vec2 GetScreenPos(vec3 pos)
{
    return vec2(PI*dot( pos, cameraMatrix[0].xyz ),PI* dot( pos,  cameraMatrix[1].xyz ));
}

vec3 CalculateSunFlare(vec3 rayDir, vec3 rayOrigin, vec2 screenSpace, float alpha)
{

    float visibility = clamp( dot(sunPos, rayDir), 0.0, 1.0 );  
    vec2 sunScreenPos = GetScreenPos(sunPos);

    // check if center of sun is covered by any object. MATH IS OFF AT SCREEN CHECK POS! sunScreenPos/2.0 +0.5 IS NOT EXACTLY SUN MIDDLE!
    float sunTest = readAlpha( sunScreenPos/2.0 +0.5);

    vec2 uvT = screenSpace-sunScreenPos;
    float sunIntensity = (1.0/(pow(length(uvT)*4.0+1.0,1.30)))*visibility;

    vec3 flareColor;
    vec2 offSet;;
    vec3 color;
    // only draw if now covered by any object
    if(sunTest<0.50)
    {
    // create flare rings
    for(float i =1.; i<8.; i++)
    {
       color.rg = vec2(0.75+(0.25*sin(i*i)));
       color.b = 0.75+(0.25*cos(i*i));
       offSet = mix (uvT, uvT+sunScreenPos, 0.67*i);
       flareColor += sunIntensity *4.25 * color * pow(max(1.0-(length(offSet)/(1.0+(i*0.2)))*4.60, 0.0),3.0);    
    }
    }
	// flare star shape
	vec3 sunSpot = vec3(1.30, 1., .80)*sunIntensity*(sin(FastNoise((sunScreenPos.x+sunScreenPos.y)*5.+atan(uvT.x,uvT.y)*10.)*4.0)*.2+3.5*sunIntensity);
	// sun glow
	sunSpot+=vec3(visibility,visibility*0.76,visibility*0.20)*pow(visibility,6.0)*0.35;

    return flareColor+(sunSpot*(1.0-alpha));
}


void pR(inout vec2 p, float a) {
  p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}


void mainImage( out vec4 fragColor, vec2 fragCoord )
{  
  vec2 mo = iMouse.xy/iResolution.xy;
  vec2 uv = fragCoord.xy / iResolution.xy;
  vec2 screenSpace = (-iResolution.xy + 2.0*(fragCoord))/iResolution.y;

  // read values from buffer
  float turn = mix(0.4, read(vec2(1.0, 10.0)), step(1.0, float(iFrame)));
  float roll = mix(3.14, read(vec2(1.0, 1.0)), step(1.0, float(iFrame)));
  float speed = read(vec2(10.0, 1.0));
  float pitch = read(vec2(15.0, 1.0));
  sunPos = readRGB(vec2(50.0, 50.0));

  // setup camera and ray direction
  vec2 camrot = vec2(1.9, 0.25);
  camrot.x+=mo.x*16.; 
  camrot.y+=mo.y*16.; 


   vec3 rayOrigin = vec3(CAMZOOM*cos(camrot.x), 3.+CAMZOOM*sin(camrot.y), -3.+CAMZOOM*sin(camrot.x) );
  pR(rayOrigin.xz, -turn);
 cameraMatrix  = setCamera( rayOrigin, vec3(0., 0., -3. ), 0.0 );
  vec3 rayDir = cameraMatrix * normalize( vec3(screenSpace.xy, 2.0) );


  vec4 color = texture(iChannel2, uv);
  color.rgb += CalculateSunFlare(rayDir, rayOrigin,screenSpace, color.a);


     // fragColor = vec4(mix(colorOld.rgb, color.rgb, mix(1.0, MAXBLUR, smoothstep(0.3, 1., speed))), fragColor.a);
  fragColor =  vec4(pow(color.rgb, vec3(1.0/1.1)), 1.0 ) * (0.5 + 0.5*pow( 16.0*uv.x*uv.y*(1.0-uv.x)*(1.0-uv.y), 0.2 ));
}

