  // SET TO 1.0 TO DISABLE BLUR
  #define MAXBLUR 0.35    
  // remove shadow and quality tree define if FPS is too low
  #define SHADOWS
  #define QUALITY_TREE

  #define CAMZOOM  13.9
  #define keyClick(ascii)   ( texelFetch(iChannel1, ivec2(ascii, 0), 0).x > 0.)
  #define read(memPos) (  textureLod(iChannel2, (memPos+0.5)/iResolution.xy, 0.0).a)
  #define readRGB(memPos) (textureLod(iChannel2, (memPos+0.5)/iResolution.xy, 0.0).rgb)
  #define MAX_HEIGHT 80. 
  #define MIN_HEIGHT -12. 
  #define STARTHEIGHT 10.
  #define WATER_LOD 1.
  #define TERRAINLEVEL 28.
  #define CLOUDLEVEL -32.0

vec3 sunPos;
const vec3 eps = vec3(0.02, 0.0, 0.0);
vec3 planePos;

struct RayHit
{
  bool hit;  
  vec3 hitPos;
  vec3 normal;
  float dist;
  float depth;
};

struct Missile
{ 
  vec3 pos;
  vec3 startPos;
  float life;
  float roll;
  float pitch;
};

Missile missile01;    

float hash(float h) 
{
  return fract(sin(h) * 43758.5453123);
}

float noise(vec3 x) 
{
  vec3 p = floor(x);
  vec3 f = fract(x);
  f = f * f * (3.0 - 2.0 * f);

  float n = p.x + p.y * 157.0 + 113.0 * p.z;
  return -1.0+2.0*mix(
    mix(mix(hash(n + 0.0), hash(n + 1.0), f.x), 
    mix(hash(n + 157.0), hash(n + 158.0), f.x), f.y), 
    mix(mix(hash(n + 113.0), hash(n + 114.0), f.x), 
    mix(hash(n + 270.0), hash(n + 271.0), f.x), f.y), f.z);
}
float fbm(vec3 p) 
{
  float f = 0.5000 * noise(p);
  p *= 2.01;
  f += 0.2500 * noise(p);
  p *= 2.02;
  f += 0.1250 * noise(p);
  return f;
}


float noise2D( in vec2 pos, float lod)
{
  vec2 f = fract(pos);
  f = f*f*(3.0-2.0*f);
  vec2 rg = textureLod( iChannel0, (((floor(pos).xy+vec2(37.0, 17.0)) + f.xy)+ 0.5)/256.0, lod).yx;  
  return -1.0+2.0*mix( rg.x, rg.y, 0.5 );
}
float noise2D( in vec2 pos )
{
  return noise2D(pos, 0.0);
}

vec3 calcWaterNormal( vec2 pos, float res )
{   
  return normalize(vec3(noise2D((pos + vec2(-0.001, 0))* res, WATER_LOD)-noise2D((pos + vec2(+0.001, 0))* res, WATER_LOD), noise2D((pos + vec2(0, -0.001))*res, WATER_LOD)-noise2D((pos + vec2(0, +0.001))* res, WATER_LOD), .005)) * 0.5 + 0.5;
}

float GetCloudHeight(vec3 p)
{    
  vec2 p2 = (p.xz+planePos.xz)*0.04;

  float i  = 0.3+noise2D(p2)*1.4; 
  p2*=2.02;
  i += noise2D( p2 )*0.6; 
  p2*=2.03;
  i += noise2D( p2 )*0.25; 
  p2*=2.51;
  i += noise2D(p2 )*0.12;
  p2*=3.12;
  i += noise2D( p2)*0.04;
  return clamp(i, -2.0, 8.)*3.50;
}


float GetTerrainHeight( vec3 p)
{
  vec2 p2 = (p.xz+planePos.xz)*0.0005;

  float mainHeight = -1.3+abs(fbm((p+vec3(planePos.x,0.,planePos.z))*0.01))*16.; 
  float terrainHeight=mainHeight;
  p2*=4.0;
  terrainHeight += textureLod( iChannel3, p2, 2.7 ).x*1.; 
  p2*=2.0;
  terrainHeight -= textureLod( iChannel3, p2, 1.2 ).x*.7;
  p2*=3.0;
  terrainHeight -= textureLod( iChannel3, p2, 0.5 ).x*.1;

  terrainHeight=mix(terrainHeight, mainHeight*1.4, smoothstep(1.5, 3.5, terrainHeight)); 

  return   terrainHeight;
}


float MapTerrain( vec3 p)
{
  float terrainHeight = GetTerrainHeight(p);   
  terrainHeight= mix(terrainHeight+(textureLod(iChannel3, (p.xz+planePos.xz)*0.02, 1.6).x*0.4), terrainHeight, smoothstep(0.4, 1.3, terrainHeight));

  float tree = mix(0., mix(0., 2.0, smoothstep(0.3, 0.86, textureLod(iChannel3, (p.xz+planePos.xz)*0.02, 1.0).x)), smoothstep(1.5, 3.5, terrainHeight)); 
  tree -= tree*0.75;
  tree*=4.0;

  return   p.y+TERRAINLEVEL - (terrainHeight+tree);
}


float SoftShadow( in vec3 origin, in vec3 direction )
{
  float res = 2.0, t = 0.0, h;
  for ( int i=0; i<16; i++ )
  {
    h = MapTerrain(origin+direction*t);
    res = min( res, 3.5*h/t );
    t += clamp( h, 0.02, 0.8);
    if ( h<0.002 ) break;
  }
  return clamp( res, 0.0, 1.0 );
}


vec3 calcNormal( in vec3 pos )
{    
  return normalize( vec3(MapTerrain(pos+eps.xyy) - MapTerrain(pos-eps.xyy), 0.5*2.0*eps.x, MapTerrain(pos+eps.yyx) - MapTerrain(pos-eps.yyx) ) );
}

float MapCloud( vec3 p)
{
  return CLOUDLEVEL + p.y - GetCloudHeight(p);
}

float MapCloudBelow( vec3 p)
{
  return CLOUDLEVEL + p.y - -GetCloudHeight(p);
}

float GetTreeHeight( vec3 p, float terrainHeight)
{
  float tree = mix(0., mix(0., 2.0, smoothstep(0.3, 0.86, textureLod(iChannel3, (p.xz+planePos.xz)*0.02, 1.0).x)), smoothstep(1.5, 3.5, terrainHeight)); 
  tree -= tree*0.75;
  tree*=4.0;

  return  tree;
}

float MapTree( vec3 p)
{  
  float terrainHeight = GetTerrainHeight(p);
  float treeHeight =GetTreeHeight(p, terrainHeight);

  // get terrain height at position and tree height onto that
  return  p.y + TERRAINLEVEL - terrainHeight-treeHeight;
}

vec4 MarchTrees( vec3 origin, vec3 direction, int steps)
{
  vec4 treeCol =vec4(0.5, 0.5, 0.5, 0.0);
  float intensity=0.0, t = .0, dist = 0.0;
  vec3 rayPos, nn;
  float precis=.0, dif =0.0, densAdd =.0;
  float treeHeight = 0.0, terrainHeight =0.0;

  for ( int i=0; i<steps; i++ )
  {
    rayPos = origin+direction*t;
    terrainHeight = GetTerrainHeight(rayPos);
    treeHeight = GetTreeHeight(rayPos, terrainHeight);
    dist = rayPos.y + TERRAINLEVEL - (terrainHeight + treeHeight);  
    precis = 0.001;

    if (treeHeight>0.1 && dist<precis)
    {
      nn= calcNormal(rayPos);  
      dif = clamp( dot( nn, sunPos ), 0.0, 1.0 );
      densAdd = ((precis-dist)*treeHeight+(0.001*t));

      treeCol.rgb+=0.03*(0.1*t);
      treeCol.a+=(1.-treeCol.a)*densAdd;
    } 
    if (treeCol.a > 0.99) 
    {
      break;
    }

    t += dist*0.5;
  }

  return clamp(treeCol, 0., 1.);
}

vec4 MarchClouds( vec3 origin, vec3 direction, int steps)
{
  vec4 cloudCol =vec4(0.7, 0.7, 0.7, 0.0);
  float height = 0.0, t = .0, dist = 0.0;
  vec3 rayPos;
  float precis; 
  for ( int i=0; i<steps; i++ )
  {
    rayPos = origin+direction*t;
    height = GetCloudHeight(rayPos);
    dist = CLOUDLEVEL + rayPos.y - height;      
    precis = 0.01*t;
    if (dist<precis)
    {     
      cloudCol.rgb+=MapCloud(rayPos+0.25*sunPos)*0.02;

      float densAdd = mix(0., (precis-dist)*0.15, smoothstep(-1., 1.7, height));
      cloudCol.a+=(1.-cloudCol.a)*densAdd;
    } 

    if (cloudCol.a > 0.99) break; 

    t += dist*0.33;
  }
  return clamp(cloudCol, 0., 1.);
}


vec4 MarchCloudsBelow( vec3 origin, vec3 direction, int steps)
{
  vec4 cloudCol =vec4(0.9, 0.9, 0.9, 0.0);
  float height = 0.0, t = .0, dist = 0.0;
  vec3 rayPos;
  float precis; 
  for ( int i=0; i<steps; i++ )
  {
    rayPos = origin+direction*t;
    height = GetCloudHeight(rayPos)*0.43;
    dist = abs(CLOUDLEVEL + rayPos.y + height);      

    precis = 0.007*t;
    if (height>0.01 && dist<precis)
    {     
      cloudCol.rgb+=MapCloudBelow(rayPos+0.26*sunPos)*0.0005;

      float densAdd = mix(0., (precis-dist)*0.04, smoothstep(0., 2., height));
      cloudCol.a+=(1.-cloudCol.a)*densAdd;
    } 

    if (cloudCol.a > 0.99) break; 

    t += dist*0.35;
  }
  return clamp(cloudCol, 0., 1.);
}


RayHit MarchTerrain( vec3 origin, vec3 direction, int steps)
{
  RayHit result;
  float precis = 0.0, maxDist = 350.0, t = 0.0, dist = 0.0;
  vec3 rayPos;

  for ( int i=0; i<steps; i++ )
  {
    rayPos =origin+direction*t; 
    dist = MapTerrain( rayPos);
    precis = 0.001*t;

    if (dist<precis || t>maxDist )
    {             
      result.hit=!(t>maxDist);
      result.depth = t; 
      result.dist = dist;                              
      result.hitPos = origin+((direction*t));     
      break;
    }

    t += dist*0.5;
  }

  return result;
}

mat3 setCamera(  vec3 ro, vec3 ta, float cr )
{
  vec3 cw = normalize(ta-ro);
  vec3 cp = vec3(sin(cr), cos(cr), 0.0);
  vec3 cu = normalize( cross(cw, cp) );
  vec3 cv = normalize( cross(cu, cw) );
  return mat3( cu, cv, cw );
}


// set sky color tone. 2 gradient passes using MIX.
vec3 GetSkyColor(vec3 rayDir)
{ 
    /*
    rayDir.y+=0.1;
    vec3  skyColor = vec3(0.2, 0.2, 0.3);
    skyColor = mix(vec3(0.16, 0.47, 0.75),skyColor,smoothstep(0.0, 0.8, rayDir.y));
   skyColor = mix(vec3(0.8,.8,0.84),skyColor,smoothstep(0., 0.35, rayDir.y));
   skyColor = mix(vec3(1.),skyColor,smoothstep(-0.06, 0.14, rayDir.y));  
   skyColor = mix(vec3(0.87,.92,1.),skyColor,smoothstep(-0.16, 0.0, rayDir.y));  
    return skyColor;
*/
         return mix(mix(vec3(0.04, 0.1, 0.2), vec3(0.16, 0.36, 0.55)*1.2, smoothstep(1.0, .30, rayDir.y)), vec3(0.99), smoothstep(0.3, -0.13, rayDir.y)); 

 }

// scene lightning
vec3 GetSceneLight(float specLevel, vec3 normal, RayHit rayHit, vec3 rayDir, vec3 origin)
{                
  vec3 reflectDir = reflect( rayDir, normal );

  float amb = clamp( 0.5+0.5*normal.y, 0.0, 1.0 );
  float dif = clamp( dot( normal, sunPos ), 0.0, 1.0 );
  float bac = clamp( dot( normal, normalize(vec3(-sunPos.x, 0.0, -sunPos.z))), 0.0, 1.0 )*clamp( 1.0-rayHit.hitPos.y, 0.0, 1.0);
  float fre = pow( clamp(1.0+dot(normal, rayDir), 0.0, 1.0), 2.0 );
  specLevel*= pow(clamp( dot( reflectDir, sunPos ), 0.0, 1.0 ), 16.0);

  float skylight = smoothstep( -0.1, 0.1, reflectDir.y );
  #ifdef SHADOWS
    vec3 shadowPos = origin+((rayDir*rayHit.depth)*0.99); 
  dif *= SoftShadow(shadowPos, sunPos);
  #endif

    vec3 lightTot = vec3(0.0);
  lightTot += 1.30*dif*vec3(1.00, 0.80, 0.55);
  lightTot += 0.50*skylight*vec3(0.40, 0.60, 1.00);
  lightTot += 1.20*specLevel*vec3(0.9, 0.8, 0.7)*dif;
  lightTot += 0.50*bac*vec3(0.25, 0.25, 0.25);
  lightTot += 0.25*fre*vec3(1.00, 1.00, 1.00);
  lightTot += 0.45*amb*vec3(0.6,0.6, .80);

  return lightTot;
}

void pR(inout vec2 p, float a) {
  p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}


void mainImage( out vec4 fragColor, vec2 fragCoord )
{  
  vec2 mo = iMouse.xy/iResolution.xy;
  vec2 uv = fragCoord.xy / iResolution.xy;
  vec2 screenSpace = (-iResolution.xy + 2.0*(fragCoord))/iResolution.y;

 // vec4 colorOld =  texture(iChannel2, uv);

  // read values from buffer

  float turn = mix(0.4, read(vec2(1.0, 10.0)), step(1.0, float(iFrame)));
  float roll = mix(3.14, read(vec2(1.0, 1.0)), step(1.0, float(iFrame)));
  float speed = read(vec2(10.0, 1.0));
  float pitch = read(vec2(15.0, 1.0));
  sunPos = mix(normalize(vec3(2.3, 1.7, -1.)), readRGB(vec2(50.0, 50.0)), step(1.0, float(iFrame)));
  planePos = mix(vec3(-400,STARTHEIGHT,-100), readRGB(vec2(55, 50.0)), step(1.0, float(iFrame)));

  roll=mod(roll, 6.28);

  float turnAmount = mix(0., 1.57, smoothstep(0., 1.57, 1.57-distance(1.57, roll-3.14)));
  turnAmount += mix(0., -1.57, smoothstep(0., 1.57, 1.57-distance(-1.57, roll-3.14)));

  turn+=turnAmount*0.0175;
  planePos.xz += vec2(cos(turn+1.5707963)*0.5,  sin(turn+1.5707963)*0.5)*(0.7+speed);
    planePos.y = clamp(planePos.y+(pitch*0.25), MIN_HEIGHT, MAX_HEIGHT);


  // setup camera and ray direction
  vec2 camrot = vec2(1.9, 0.25);
  camrot.x+=mo.x*16.; 
  camrot.y+=mo.y*16.; 

  vec3 rayOrigin = vec3(CAMZOOM*cos(camrot.x), 3.+planePos.y+CAMZOOM*sin(camrot.y), -3.+CAMZOOM*sin(camrot.x) );
  pR(rayOrigin.xz, -turn);
  mat3 ca = setCamera( rayOrigin, vec3(0., planePos.y, -3. ), 0.0 );
  vec3 rayDir = ca * normalize( vec3(screenSpace.xy, 2.0) );


  // create sky color fade
  vec3 skyColor = GetSkyColor(rayDir);
  vec3 color = skyColor;
     float alpha=0.;

    if(rayDir.y<0.)
      {
  RayHit marchResult = MarchTerrain(rayOrigin, rayDir, 250);


  if (marchResult.hit)
  { 
      alpha=1.0;
    marchResult.normal = calcNormal(marchResult.hitPos);  

    float specLevel=0.7;
    color=vec3(0.3, 0.2, .0);

    // create terrain texture
    vec3 colorRocks= mix(texture(iChannel3, (marchResult.hitPos.xz+planePos.xz)*.007).rgb, vec3(0.6), 0.5);
    color =colorRocks;

    // grass
    color.rgb = mix(color.rgb, ((color+noise2D((marchResult.hitPos.xz+planePos.xz)*12., 1.0))+vec3(0.5, 0.5, .0))*0.3, smoothstep(-TERRAINLEVEL+0.2, -TERRAINLEVEL+2.0, marchResult.hitPos.y)); 

      if(marchResult.hitPos.y<=-TERRAINLEVEL+0.25)
      {
    // add ripples to water
    vec3 waterNormal = (calcWaterNormal(marchResult.hitPos.xz+planePos.xz + vec2(-iTime*0.4, -iTime*0.2), 2.66));
    waterNormal = (normalize(mix(waterNormal,(calcWaterNormal(marchResult.hitPos.xz+planePos.xz + vec2(iTime*0.4, iTime*0.2), 5.61)),0.5))+vec3(0.,1.0,0.))*0.4;    
       marchResult.normal = mix(waterNormal, marchResult.normal, smoothstep(-TERRAINLEVEL-0.1, -TERRAINLEVEL+0.25, marchResult.hitPos.y));

    //vec3 waterNormal = ((calcWaterNormal(marchResult.hitPos.xz+planePos.xz + vec2(-iTime*0.4, -iTime*0.2), 2.66))*(calcWaterNormal(marchResult.hitPos.xz+planePos.xz + vec2(iTime*0.4, iTime*0.2), 5.61)));      
   // marchResult.normal = mix((vec3(0.0, 1.0, 0.0)+waterNormal)*0.4, marchResult.normal, smoothstep(-TERRAINLEVEL+0.2, -TERRAINLEVEL+0.5, marchResult.hitPos.y));


    // water color
 color.rgb  = mix(skyColor, color.rgb, smoothstep(-TERRAINLEVEL-0.7, -TERRAINLEVEL+0.3, marchResult.hitPos.y));
    color.rgb  = mix(skyColor*0.65, color.rgb, smoothstep(-TERRAINLEVEL-1.39, -TERRAINLEVEL+0.4, marchResult.hitPos.y));
     specLevel = 22.0;
      }

    // get lightning based on material
    vec3 light = GetSceneLight(specLevel, marchResult.normal, marchResult, rayDir, rayOrigin);   

    // apply lightning
    color = color*light;

    #ifdef QUALITY_TREE
      // add trees
      vec4 treeColor = MarchTrees(rayOrigin, rayDir, 32);      
    color =clamp( mix( color, treeColor.rgb*((noise2D((marchResult.hitPos.xz+planePos.xz)*36., 2.0)+vec3(0.5, 0.5, .0))*0.5), treeColor.a ), 0.1, 1.); 
    #endif


      color = mix(color, vec3(0.3, 0.5, 0.7), mix(0.1, 0.45, smoothstep(MIN_HEIGHT, MAX_HEIGHT, planePos.y)));  

    // add fog
    color = mix(color, skyColor, smoothstep(150., 1250., (marchResult.depth*marchResult.depth)*0.01));
  }
      }
  // add volumetric clouds 
  vec4 cloudColor;
  if (rayOrigin.y>=-CLOUDLEVEL)
  {   
      if(rayDir.y<0.)
      {
    cloudColor= MarchClouds(rayOrigin, rayDir, 70);
      }
  }
    else
  {
      if(rayDir.y>0.)
      {
    cloudColor=MarchCloudsBelow(rayOrigin, rayDir, 70);
      }
  }

  // mix clouds color with scene color
  color.rgb = mix(color.rgb, cloudColor.rgb, cloudColor.a);

    alpha+=cloudColor.a;
  pitch*=0.97;


 // fragColor = vec4(mix(colorOld.rgb, color.rgb, mix(1.0, MAXBLUR, smoothstep(0.3, 1., speed))), fragColor.a);
    fragColor = vec4(color.rgb, min(1.0,alpha));
}

