mat3 setCamera(  vec3 ro, vec3 ta, float cr )
{
  vec3 cw = normalize(ta-ro);
  vec3 cp = vec3(sin(cr), cos(cr), 0.0);
  vec3 cu = normalize( cross(cw, cp) );
  vec3 cv = normalize( cross(cu, cw) );
  return mat3( cu, cv, cw );
}


void pR(inout vec2 p, float a) {
  p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}


void mainImage( out vec4 fragColor, vec2 fragCoord )
{  
  vec2 mo = iMouse.xy/iResolution.xy;
  vec2 uv = fragCoord.xy / iResolution.xy;
  vec2 screenSpace = (-iResolution.xy + 2.0*(fragCoord))/iResolution.y;

 // vec4 colorOld =  texture(iChannel2, uv);

  // read values from buffer
  float turn = mix(0.4, read(vec2(1.0, 10.0)), step(1.0, float(iFrame)));
  float roll = mix(3.14, read(vec2(1.0, 1.0)), step(1.0, float(iFrame)));
  float speed = read(vec2(10.0, 1.0));
  float pitch = read(vec2(15.0, 1.0));
  sunPos = mix(normalize(vec3(2.3, 1.7, -1.)), readRGB(vec2(50.0, 50.0)), step(1.0, float(iFrame)));
  planePos = mix(vec3(-400,STARTHEIGHT,-100), readRGB(vec2(55, 50.0)), step(1.0, float(iFrame)));


  roll=mod(roll, 6.28);
  float turnAmount = mix(0., 1.57, smoothstep(0., 1.57, 1.57-distance(1.57, roll-3.14)));
  turnAmount += mix(0., -1.57, smoothstep(0., 1.57, 1.57-distance(-1.57, roll-3.14)));

  turn+=turnAmount*0.0175;
  planePos.xz += vec2(cos(turn+1.5707963)*0.5,  sin(turn+1.5707963)*0.5)*(0.7+speed);
  planePos.y = clamp(planePos.y+(pitch*0.25), MIN_HEIGHT, MAX_HEIGHT);


  // setup camera and ray direction
  vec2 camrot = vec2(1.9, 0.25);
  camrot.x+=mo.x*16.; 
  camrot.y+=mo.y*16.; 

  vec3 rayOrigin = vec3(CAMZOOM*cos(camrot.x), 3.+planePos.y+CAMZOOM*sin(camrot.y), -3.+CAMZOOM*sin(camrot.x) );
  pR(rayOrigin.xz, -turn);
  mat3 ca = setCamera( rayOrigin, vec3(0., planePos.y, -3. ), 0.0 );
  vec3 rayDir = ca * normalize( vec3(screenSpace.xy, 2.0) );

vec3 color= vec3(0.);
  pitch*=0.97;

  // check key inputs. S and F controls roll. E and D controls speed.
  roll-=0.03*float(keyClick(83)); //s-key
  roll+=0.03*float(keyClick(70)); //f-key
  speed=clamp(speed+(0.02*float(keyClick(82))), -0.3, 1.); //r-key
  speed=clamp(speed-(0.02*float(keyClick(87))), -0.3, 1.); //w-key
  pitch=clamp(pitch-(mix(0.02, 0., smoothstep(0., 3., 3.0-abs(distance(planePos.y, MIN_HEIGHT))))*float(keyClick(69))), -0.5, 0.5); //e-key
  pitch=clamp(pitch+(mix(0.02, 0., smoothstep(0., 3., 3.0-abs(distance(planePos.y, MAX_HEIGHT))))*float(keyClick(68))), -0.5, 0.5); //d-key


  turnAmount += mix(0., -1.57, smoothstep(0., 1.57, 1.57-distance(-1.57, roll-3.14)));

  fragColor.a=0.;
  // save roll,speed and scroll values to buffer A 
  fragColor.a = mix(turn, fragColor.a, step(1., distance(fragCoord.xy, vec2(1.0, 10.0)))); 
  fragColor.a = mix(speed, fragColor.a, step(1., distance(fragCoord.xy, vec2(10.0, 1.0)))); 
  fragColor.a = mix(roll, fragColor.a, step(1., distance(fragCoord.xy, vec2(1.0, 1.0)))); 
  fragColor.a = mix(pitch, fragColor.a, step(1., distance(fragCoord.xy, vec2(15.0, 1.0)))); 
  color.rgb = mix(sunPos, color.rgb, step(1., distance(fragCoord.xy, vec2(50.0, 50.0))));
  color.rgb = mix(planePos, color.rgb, step(1., distance(fragCoord.xy, vec2(55.0, 50.0))));

/*
  // missile 
