  missile01.life = read(vec2(100.0, 100.0));
  missile01.roll = read(vec2(108.0, 100.0));
  missile01.pitch = read(vec2(110.0, 100.0));

  // if missile is "dead" check if a new missile is being lanched by pressing the key
  if ( missile01.life<=0.)
  {

    if (keyClick(77))
    {
      missile01.life=3.0; //m-key
      missile01.roll = roll;
      missile01.pitch =pitch;
    }
  }

  // update active missile and save variables
  if ( missile01.life>0.)
  {
    missile01.life-= iTimeDelta;

    fragColor.a = mix(missile01.life, fragColor.a, step(1., distance(fragCoord.xy, vec2(100.0, 100.0))));
    fragColor.a = mix(missile01.roll, fragColor.a, step(1., distance(fragCoord.xy, vec2(108.0, 100.0))));
    fragColor.a = mix(missile01.pitch, fragColor.a, step(1., distance(fragCoord.xy, vec2(110.0, 100.0))));
  }
  */ 
