float Map( vec3 p)
{
  float  d=100000.0;
  vec3 pOriginal = p;
  // rotate position 
  p=TranslatePos(p, pitch, roll);

  if (min(d, sdBox( p- vec3(0., -0., -3.), vec3(7.5, 2., 7.6)))<20.0) //Bounding Box test
  { 
    // mirror position at x=0.0. Both sides of the plane are equal.
    pMirror(p.x, 0.0);

    float body= min(d, sdEllipsoid(p-vec3(0., 0.1, -4.40), vec3(0.50, 0.30, 2.)));
    body=fOpUnionRound(body, sdEllipsoid(p-vec3(0., 0., .50), vec3(0.50, 0.40, 3.25)), 1.);
    body=min(body, sdConeSection(p- vec3(0., 0., 3.8), 0.7, 0.07, 0.01));   

    // window
    winDist =sdEllipsoid(p-vec3(0., 0.3, -0.10), vec3(0.45, 0.4, 1.45));
    winDist =fOpUnionRound(winDist, sdEllipsoid(p-vec3(0., 0.3, 0.60), vec3(0.3, 0.6, .75)), 0.4);
    winDist = max(winDist, -body);
    body = min(body, winDist);
    body=min(body, fOpPipe(winDist, sdBox(p-vec3(0., 0., 1.0), vec3(3.0, 1., .01)), 0.03));
    body=min(body, fOpPipe(winDist, sdBox(p-vec3(0., 0., .0), vec3(3.0, 1., .01)), 0.03));

    // front
    checkPos = p-vec3(0, 0, 2.5);
    pR(checkPos.yz, 1.57);
    body=fOpIntersectionRound(body, -sdTorus(checkPos, vec2(.46, 0.05)), 0.015);
    body=fOpIntersectionRound(body, -sdTorus(checkPos+vec3(0, 0.80, 0), vec2(.6, 0.05)), 0.015);
    body=fOpIntersectionRound(body, -sdTorus(checkPos+vec3(0, 2.30, 0), vec2(.62, 0.06)), 0.015);

    // wings    
    frontWingDist = MapFrontWing(p);
    d=min(d, frontWingDist);
    rearWingDist = MapRearWing(p);
    d=min(d, rearWingDist);
    topWingDist = MapTopWing(p);
    d=min(d, topWingDist);

    // bottom
    checkPos = p-vec3(0., -0.6, -5.0);
    pR(checkPos.yz, 0.07);  
    d=fOpUnionRound(d, sdBox(checkPos, vec3(0.5, 0.2, 3.1)), 0.40);

    float holder = sdBox( p- vec3(0., -1.1, -4.30), vec3(0.08, 0.4, 0.8));  
    checkPos = p;
    pR(checkPos.yz, 0.85);
    holder=max(holder, -sdBox( checkPos- vec3(0., -5.64, -2.8), vec3(1.75, 1.4, 1.0))); 
    d=fOpUnionRound(d, holder, 0.25);

    // large bomb
    bombDist2 = fCylinder( p- vec3(0., -1.6, -4.0), 0.45, 1.);   
    bombDist2 =min(bombDist2, sdEllipsoid( p- vec3(0., -1.6, -3.20), vec3(0.45, 0.45, 2.)));   
    bombDist2 =min(bombDist2, sdEllipsoid( p- vec3(0., -1.6, -4.80), vec3(0.45, 0.45, 2.)));   

    d=min(d, bombDist2);

    d=min(d, sdEllipsoid(p- vec3(1.05, 0.13, -8.4), vec3(0.11, 0.18, 1.0)));    

    checkPos = p- vec3(0, 0.2, -5.0);
    d=fOpUnionRound(d, fOpIntersectionRound(sdBox( checkPos, vec3(1.2, 0.14, 3.7)), -sdBox( checkPos, vec3(1., 1.14, 4.7)), 0.2), 0.25);

    d=fOpUnionRound(d, sdEllipsoid( p- vec3(0, 0., -4.), vec3(1.21, 0.5, 2.50)), 0.75);

    // engine cutout
    blackDist = max(d, fCylinder(p- vec3(.8, -0.15, 0.), 0.5, 2.4)); 
    d=max(d, -fCylinder(p- vec3(.8, -0.15, 0.), 0.5, 2.4)); 

    // engine
    d =max(d, -sdBox(p-vec3(0., 0, -9.5), vec3(1.5, 0.4, 0.7)));

    engineDist=fCylinder(p- vec3(0.40, -0.1, -8.7), .42, 0.2);
    checkPos = p- vec3(0.4, -0.1, -8.3);
    pR(checkPos.yz, 1.57);
    engineDist=min(engineDist, sdTorus(checkPos, vec2(.25, 0.25)));
    engineDist=min(engineDist, sdConeSection(p- vec3(0.40, -0.1, -9.2), 0.3, .22, .36));

    checkPos = p-vec3(0., 0., -9.24);  
    checkPos.xy-=vec2(0.4, -0.1);
    checkPos.xy = pModPolar(checkPos.xy, 22.0);

    float engineCone = fOpPipe(engineDist, sdBox( checkPos, vec3(.6, 0.001, 0.26)), 0.015);
    engineDist=min(engineDist, engineCone);
    engineDist=fOpIntersectionRound(engineDist, -fCylinder(p- vec3(0.4, -0.1, -9.52), 0.22, 1.0), 0.02);  

    d=min(d, engineDist);

    d=min(d, winDist);
    d=min(d, body);
    eFlameDist = sdEllipsoid( p- vec3(0.4, -0.1, -9.35-(speed*0.07)+cos(iTime*90.0)*0.03), vec3(.17, 0.17, .20));

    d=min(d, sdBox( p- vec3(1.1, 0., -6.90), vec3(.33, .12, .17))); 

    checkPos = p-vec3(0.65, 0.55, -1.4);
    pR(checkPos.yz, -0.35);
    d=min(d, sdBox(checkPos, vec3(0.2, 0.1, 0.45)));
  }
  /*
    // check if missile is launched
   if(missile01.life>0.0)
   {     
   // create translation based on launch pitch and roll
   p= TranslatePos(pOriginal, missile01.pitch, missile01.roll);
   // mirror position to create two missiles at once  
   pMirror(p.x, 0.0);

   vec3 distanceTraveled = (3.-missile01.life)*vec3(0.,0,-30.0);
   checkPos = p-vec3(4.7, -0.45, -4.50)+distanceTraveled;
   d = min(d, MapMissile(checkPos));
   // map missile flame
   eFlameDist = min(eFlameDist,sdEllipsoid(checkPos+ vec3(0.,0.,2.+cos(iTime*90.0)*0.23), vec3(.14, 0.14, 1.20)));
   d = min(d, eFlameDist);

   // map missile smoke trail

   checkPos =  p-vec3(4.7, -0.45, -2.50)+distanceTraveled;
   float s = pModInterval1(checkPos.z, -0.7, .0, 35.0);

   d=min(d,sdEllipsoid(checkPos, vec3(.3, 0.3, .15)*max(0.,((s*0.02)))));
   }
   */
  return min(d, eFlameDist);
}


vec3 calcNormal( in vec3 pos )
{    
  return normalize( vec3(Map(pos+eps.xyy) - Map(pos-eps.xyy), 0.5*2.0*eps.x, Map(pos+eps.yyx) - Map(pos-eps.yyx) ) );
}


float SoftShadow( in vec3 origin, in vec3 direction )
{
  float res = 2.0, t = 0.0, h;
  for ( int i=0; i<32; i++ )
  {
    h = Map(origin+direction*t);
    res = min( res, 8.5*h/t );
    t += clamp( h, 0.03, 0.1);
    if ( h<0.002 ) break;
  }
  return clamp( res, 0.0, 1.0 );
}


RayHit March(in vec3 origin, in vec3 direction)
{
  RayHit result;
  float maxDist = 150.0;
  float t = 0.0, glassDist = 0.0, dist = 0.0;
  vec3 rayPos;

  for ( int i=0; i<RAYSTEPS; i++ )
  {
    rayPos =origin+direction*t;
    dist = Map( rayPos);

    if (abs(dist)<0.003 || t>maxDist )
    {                
      result.hit=!(t>maxDist);
      result.depth = t; 
      result.dist = dist;                              
      result.hitPos = origin+((direction*t));   
      result.winDist = winDist;
      result.engineDist = engineDist;
      result.eFlameDist = eFlameDist;
      result.blackDist = blackDist;
      result.bombDist = bombDist;
      result.bombDist2 = bombDist2;
      result.missileDist = missileDist;
      result.frontWingDist = frontWingDist;
      result.rearWingDist = rearWingDist;
      result.topWingDist = topWingDist;
      break;
    }
    t += dist;
  }

  return result;
}


mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
  vec3 cw = normalize(ta-ro);
  vec3 cp = vec3(sin(cr), cos(cr), 0.0);
  vec3 cu = normalize( cross(cw, cp) );
  vec3 cv = normalize( cross(cu, cw) );
  return mat3( cu, cv, cw );
}


// Advanced lightning pass. 2X SoftShadows + AO pass.
vec3 GetSceneLight(float specLevel, vec3 normal, RayHit rayHit, vec3 rayDir, vec3 origin)
{                   
  vec3 reflectDir = reflect( rayDir, normal );
  float amb = clamp( 0.5+0.5*normal.y, 0.0, 1.0 );
  float dif = clamp( dot( normal, sunPos ), 0.0, 1.0 );
  float bac = clamp( dot( normal, normalize(vec3(-sunPos.x, 0.0, -sunPos.z))), 0.0, 1.0 )*clamp( 1.0-rayHit.hitPos.y, 0.0, 1.0);
  float fre = pow( clamp(1.0+dot(normal, rayDir), 0.0, 1.0), 2.0 );
  specLevel*= pow(clamp( dot( reflectDir, sunPos ), 0.0, 1.0 ), 16.0);

  float skylight = smoothstep( -0.1, 0.1, reflectDir.y );

  vec3 shadowPos = origin+((rayDir*rayHit.depth)*0.99);   
  dif *= SoftShadow(shadowPos, sunPos);
  skylight *=SoftShadow(shadowPos, reflectDir);

  vec3 lightTot = vec3(0.0);

  lightTot += 1.30*dif*vec3(1.00, 0.80, 0.55);
  lightTot += 0.50*skylight*vec3(0.40, 0.60, 1.00);
  lightTot += 1.20*specLevel*vec3(0.9, 0.8, 0.7)*dif;
  lightTot += 0.50*bac*vec3(0.25, 0.25, 0.25);
  lightTot += 0.25*fre*vec3(1.00, 1.00, 1.00);
  return lightTot +(0.40*amb*vec3(0.40, 0.60, 1.00));
}


vec4 GetMaterial(vec3 rayDir, inout RayHit rayHit)
{
  vec3 hitPos =TranslatePos(rayHit.hitPos, pitch, roll);
  vec3 reflectDir = reflect( rayDir, rayHit.normal );
  vec3  refMap = cubemap(iChannel0, reflectDir*0.7);

  vec3 col = vec3(0.65);
  float specLevel=1.3;

  // create camo
  vec3 noiseShade = vec3(noise((hitPos+vec3(2.0, 1.0, 0.))*.8));
  vec3 baseTexture = mix(col, mix(noiseShade*0.35, noiseShade*1.1, smoothstep(0.5, 0.52, noise(hitPos*1.26))), 0.5);
  baseTexture = mix(baseTexture, (baseTexture+noiseShade)*0.55, smoothstep(0.5, 0.52, noise((hitPos+vec3(-6.))*1.26)));

  col=baseTexture;

  // create base color mixes
  vec3 lightColor = mix(vec3(0.73), noiseShade, 0.35);
  vec3 darkColor = mix(vec3(0.15), noiseShade, 0.45);
  vec3 missilBaseCol =  mix(vec3(0.7), noiseShade, 0.45);
  vec3 missilBaseCol2 =  mix(vec3(0.5), noiseShade, 0.45);
  vec3 missilCol = mix(vec3(0.25), noiseShade, 0.25);
  vec3 missilCol2 = mix(vec3(0.15), noiseShade, 0.15);

  // front
  col = mix(col, lightColor, smoothstep(3.0, 3.02, hitPos.z));  
  col = mix(col, darkColor*0.4, smoothstep(3.08, 3.1, hitPos.z));

  // front wing stripes
  col=mix(darkColor, col, smoothstep(1.4, 1.42, distance(-6.90, hitPos.z)));
  col=mix(lightColor, col, smoothstep(1.3, 1.32, distance(-6.90, hitPos.z)));
  col=mix(darkColor, col, smoothstep(.84, 0.86, distance(-6.7, hitPos.z)));
  col=mix(lightColor, col, smoothstep(.22, 0.235, distance(-6.94, hitPos.z)));

  // front wing vertical stripes   
  float xMod = mod(hitPos.x-0.5, 11.0);
  col=mix(darkColor, col, smoothstep(0.5, 0.52, distance(5., xMod)));
  col=mix(lightColor, col, smoothstep(0.4, 0.42, distance(5., xMod)));

  col=mix(mix(lightColor, col, step(-0.01, hitPos.y)), col, step(.01, rayHit.frontWingDist));

  // create star (front wings)         
  vec2 center = vec2(5., -5.1)-vec2(xMod, hitPos.z);
  float dist = length(center);
  col=mix(lightColor, col, smoothstep(0.8, 0.82, dist));
  col=mix(darkColor, col, smoothstep(0.7, 0.72, dist));
  col=mix(lightColor, col, smoothstep(0.7, 0.72, (dist*1.15)+abs(cos( atan(center.y, center.x)*2.5)*0.3)));
  col=mix(darkColor, col, smoothstep(0.6, 0.62, (dist*1.50)+abs(cos( atan(center.y, center.x)*2.5)*0.3)));

  // rear wing stripes
  col=mix(darkColor, col, smoothstep(.55, 0.57, distance(-9.6, hitPos.z)));
  col=mix(lightColor, col, smoothstep(.5, 0.52, distance(-9.6, hitPos.z)));
  col=mix(darkColor, col, smoothstep(.4, 0.42, distance(-9.6, hitPos.z)));

  // esm pods
  col = mix(col, lightColor*0.75, smoothstep(7.02, 7.04, abs(hitPos.x)));

  // top wing stripes
  col=mix(mix(darkColor, baseTexture, smoothstep(0.55, 0.57, distance(0.85, hitPos.y))), col, smoothstep(0., .05, rayHit.topWingDist));
  col=mix(mix(lightColor, col, smoothstep(.32, 0.34, distance(0.95, hitPos.y))), col, smoothstep(0., .05, rayHit.topWingDist));

  // create star (top wings)    
  center = vec2(-8.73, 0.95)-vec2(hitPos.z, hitPos.y);
  dist = length(center); 
  col=mix(darkColor, col, smoothstep(0.24, 0.26, dist));
  col=mix(lightColor, col, smoothstep(0.24, 0.26, (dist*1.15)+abs(cos( atan(center.y, center.x)*2.5)*0.13)));

  // windows
  col=mix(0.35*refMap, col, step(.01, rayHit.winDist));
  specLevel=mix(3.5, specLevel, step(.01, rayHit.winDist));

  // engines exhaust
  col=mix(vec3(0.3)*noiseShade, col, step(.05, rayHit.engineDist));
  specLevel=mix(0.8, specLevel, step(.05, rayHit.engineDist));

  // engine flame
  col=mix(col, vec3(1.2, .55, 0.30), smoothstep(.1+(0.02*speed), 0., rayHit.eFlameDist));
  col=mix(col*0.23, col, step(.02, rayHit.blackDist));

  // small missiles
  col=mix(mix(missilBaseCol, missilCol2, smoothstep(-3.35, -3.37, hitPos.z)), col, step(.1, rayHit.missileDist));
  col=mix(mix(col, missilCol, smoothstep(-3.2, -3.22, hitPos.z)), col, step(.1, rayHit.missileDist));
  col=mix(mix(missilCol2, col, smoothstep(.32, 0.34, distance(-4.75, hitPos.z))), col, step(.1, rayHit.missileDist));
  col=mix(mix(missilBaseCol, col, smoothstep(.25, 0.27, distance(-4.75, hitPos.z))), col, step(.1, rayHit.missileDist));

  // small bombs   
  col=mix(mix(missilCol, missilBaseCol, smoothstep(1.18, 1.2, distance(-4.5, hitPos.z))), col, step(.01, rayHit.bombDist));      
  col=mix(mix(col, missilCol2, smoothstep(1.3, 1.32, distance(-4.5, hitPos.z))), col, step(.01, rayHit.bombDist));  

  // large bomb  
  col=mix(mix(missilBaseCol2, missilCol, smoothstep(1.48, 1.5, distance(-4.1, hitPos.z))), col, step(.01, rayHit.bombDist2));      
  col=mix(mix(col, missilBaseCol, smoothstep(1.6, 1.62, distance(-4.1, hitPos.z))), col, step(.01, rayHit.bombDist2));      
  col=mix(mix(missilBaseCol, col, smoothstep(0.45, 0.47, distance(-4.1, hitPos.z))), col, step(.01, rayHit.bombDist2));      


  // reflection at angle
  col=mix(col, (col+refMap)*0.3, 0.4*pow( clamp(1.0+dot(rayHit.normal, rayDir), 0.0, 1.0), 2.0 ));
  return vec4(col, specLevel);
}

