void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2. * fragCoord - iResolution.xy) / iResolution.y;
    float c1 = distance(p, vec2(0.1 + 0.5 * sin(iTime), 0.1 + 0.5 * cos(0.9 * iTime)));
    float c2 = distance(p, vec2(0.1));

    fragColor = vec4(max(1. - c1, c2), 0., 0., 1.0);
}
