#define c30 0.86602540378
#define grid 25.0
#define split 5.0
#define smooth (40.0 / iResolution.x)
#define rt (iTime * 5.)
#define PI      3.14159265359
#define count 10.0
#define cutoff 8.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 st = (fragCoord.xy -0.5 * iResolution.xy)/ iResolution.x;
    float a = (atan(st.x,st.y) + PI) /PI /2.;
    float l = length(st);
    float lf = abs(fract(l*grid)-0.5)*2.;
    float ft = (sin(-rt + l*15.0) + 1.0)/2.;
    ft = ft*ft*ft - 0.002 * grid;
    vec3 finCol = vec3(smoothstep(smooth,-smooth,lf-ft),
                      smoothstep(smooth,-smooth,lf-ft -smooth*2.),
                      smoothstep(smooth,-smooth,lf-ft-smooth*4.));
	fragColor = vec4(finCol,1.0);
}
