float calcAlpha(float direction) {
    float ondulated = sin(direction * 100.);
    return smoothstep(-.1, .1, ondulated);
}

void mainImage( out vec4 O, in vec2 u )
{
    vec4 color1 = vec4(1);
    vec4 color2 = vec4(0);
    vec2 U = u / iResolution.xy * vec2(1., .7);

    float alpha120 = calcAlpha(U.x + U.y * .5);
    float alpha0 = calcAlpha(U.y);
    float alpha60 = calcAlpha(U.x - U.y * .5);

    float alpha = alpha120 - alpha0 + alpha60;
    O = color1 * alpha + color2 * (1. - alpha);
}
