void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // uv is a x,y coord on the canvas, 0.,0. at bottom left, 1.,1. at top right
	vec2 uv = fragCoord.xy / iResolution.xy;

    // texture looks up the color of a pixel at an xy coord
    vec4 t = texture(iChannel0, uv + vec2(1., .8) * (iTime*.245));
    vec4 t2 = texture(iChannel0, uv + vec2(.5, .7) * (-iTime*.241));
    vec4 t3 = texture(iChannel0, 23.*t2.rb+iTime*.21);
    // then we set the current pixel to that color
	fragColor = t3;
}
