// Trying to learn about the kali set, ray marching etc.
// Inspired by https://www.shadertoy.com/view/XtlGDH and many other
// cool things on this site!

//#define BOUNCE 0.04
#define BOUNCE 0.0

vec2 kalidist(vec3 pos, float s)
{
    vec3 p = pos;
    vec3 param = vec3(1.0+0.8*sin(iTime*0.03+2.4),0.8,0.4)*(1.0+(BOUNCE)*s+0.5*sin(iTime*0.03));
    float d = 100.0;
    float l = 100.0;
    for (int i = 0; i < 6; i++)
    {
        p = abs(p) / dot(p,p) - param;
        d = min(d,length(p.xz));
        l = min(l,length(p.xx));
    }
    return vec2(d - 0.01,l);
}


vec3 trace(vec3 pos, vec3 dir)
{
    vec3 sx = vec3(0.1);//abs(pos)*3.0; 
    float s =
        texture(iChannel0, vec2(sx.x*0.05-0.01,0)).r+ 
        texture(iChannel0, vec2(sx.x*0.05   ,0)).r + 
        texture(iChannel0, vec2(sx.x*0.05+0.01,0)).r;
    s*=0.3333;
    float d = kalidist(pos, s).x;
    float step = 0.0;
    float l = 0.0;
    vec3 p = pos;
    for(int i = 0; i < 100; i++)
    {
        if(d>0.01)
        {
            p = p + step*dir;
            vec2 kd = kalidist(p,s);
            l+=kd.y;
            d = min(d, kd.x);

            step = d*0.1;
        }
        else
        {
            break;
        }
    }
    float v = length(pos-p);
    float v1 = 1.0-v;
    l=0.4/(0.1+l*1.0*2.0)*smoothstep(0.7,0.8,s);
    return vec3(l*l*l,l*l,l)*0.1+v*vec3(0.2,0.2,0.0) + 1.0*clamp(vec3(v1*v1,v1,v1*v1*v1),0.0,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 pi = fragCoord.xy / iResolution.x * 2.0 - vec2(1.0,iResolution.y/iResolution.x);
    pi *= 0.95;

    float t = iTime+12.0;
    float cuttime = floor(t*0.05);
    float fade = smoothstep(0.0,0.05,0.5-abs(t*0.05-cuttime-0.5));

    float ti = sin(cuttime)*t*0.03 + cuttime*17.3;
    vec3 eye = vec3(sin(ti),1,cos(ti));
    vec3 lookat = vec3(sin(ti*5.0),0,0);
    vec3 dir = normalize(lookat-eye);
    vec3 gup = vec3(0,1,0);
    vec3 right = cross(dir, gup);
    vec3 up = cross(right, dir);

    vec3 pos = right*pi.x + up*pi.y;

    vec3 c = trace(pos, dir);

    float vi = pow(1.0-length(pi),0.6);



	fragColor = fade*clamp(vi*vec4(c,1),0.0,1.0);
}
