void mainImage( out vec4 f, in vec2 w )
{
    float x = .6 + cos(.7*iTime*.1)*.5;

    vec2 p = w / iResolution.y - .5*vec2(sin(x*5.),cos(x*11.));

    for(int i=0; i<10; i++)
        p = abs(p)/dot(p,p) - x;

	f = vec4(p,p.y-p.x,1.);
}
