// https://www.shadertoy.com/view/4sfGzS
float noise(in vec3 x) {
    vec3 p = floor(x);
    vec3 f = fract(x);
	f = f*f*(3.0-2.0*f);

	vec2 uv = (p.xy+vec2(37.0,17.0)*p.z) + f.xy;
	vec2 rg = texture( iChannel0, (uv+0.5)/256.0, -100.0 ).yx;
	return mix( rg.x, rg.y, f.z );
}

float noise2(in vec3 uv) {
    float p = 2.35;
    float n = 0.0;

    n += 0.500000 * noise(uv * p); p *= 2.01;
    n += 0.250000 * noise(uv * p); p *= 2.02;
    n += 0.125000 * noise(uv * p); p *= 1.99;
    n += 0.062500 * noise(uv * p); p *= 1.97;
    n += 0.031250 * noise(uv * p); p *= 1.98;
    n += 0.015625 * noise(uv * p);
    n /= 0.884375;

    return n;
}

// https://iquilezles.org/articles/functions
float cpulse(float c, float w, float x)
{
    x = abs(x - c);
    if( x>w ) return 0.0;
    x /= w;
    return 1.0 - x*x*(3.0-2.0*x);
}

vec3 texture(in vec3 uv) {
    uv += noise2(vec3(noise2(uv - 1532.0), noise2(uv + 786.0), 0.0));
    float n = noise2(uv);

    vec3 col = vec3(0.0);

    col += vec3(96.0, 91.0, 86.0)    / 256.0 * cpulse(0.15, 0.25, n);
    col += vec3(100.0, 94.0, 157.0)  / 256.0 * cpulse(0.45, 0.15, n);
    col += vec3(172.0, 193.0, 138.0) / 256.0 * cpulse(0.5, 0.03, n);
    col += vec3(238.0, 184.0, 104.0) / 256.0 * cpulse(0.55, 0.15, n);
    col += vec3(73.0, 220.0, 177.0)  / 256.0 * cpulse(0.85, 0.25, n);

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = -1.0 + 2.0 * uv;
    uv.x *= iResolution.x / iResolution.y;

    vec3 col = texture(vec3(uv - vec2(0.0, iTime / 16.0), iTime / 64.0));
    col = sqrt(col);

	fragColor = vec4(col, 1.0);
}
