const vec2 w = vec2(2.5, 2.5);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec4 c0 = texture(iChannel0, fragCoord.xy / iResolution.xy);
    vec4 c1 = texture(iChannel0, (fragCoord.xy - w) / iResolution.xy);
    vec4 c2 = texture(iChannel0, (fragCoord.xy + w) / iResolution.xy);
    vec4 c3 = texture(iChannel0, (fragCoord.xy - w*1.5) / iResolution.xy);
    vec4 c4 = texture(iChannel0, (fragCoord.xy + w*1.5) / iResolution.xy);
    vec4 c5 = texture(iChannel0, (fragCoord.xy - w*2.0) / iResolution.xy);
    vec4 c6 = texture(iChannel0, (fragCoord.xy + w*2.0) / iResolution.xy);
    vec4 c7 = texture(iChannel0, (fragCoord.xy - w*2.5) / iResolution.xy);
    vec4 c8 = texture(iChannel0, (fragCoord.xy + w*2.5) / iResolution.xy);
    vec4 bluredImg = 0.1752 * c0 + 0.1658 * (c1 + c2) + 0.1403 * (c3 + c4) + 0.1063 * (c5 + c6) + 0.1001 * (c7 + c8);

    float base = 0.3*length( (-1.0+2.0*(fragCoord.xy / iResolution.xy) 
                        + vec2(0.5*sin(iTime), 0.5*cos(iTime)))*2.2 );

	fragColor = mix( c0, bluredImg, base );
}
