vec4 rect; // uniform
vec4 fillColor = vec4(1,0,0,1); // uniform
vec4 strokeColor = vec4(0,1,0,1); // uniform
float strokeWidth; // uniform


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    rect = vec4(0,0,iResolution); // uniform
    strokeWidth = abs(sin(iTime)) * 40.0; // uniform

	vec2 vertCoord = fragCoord.xy; // varying


    vec2 ab = rect.zw / 2.0;
	vec2 center = rect.xy + ab;
    vec2 pos = vertCoord  - center;
    pos *= pos;
    float outerDist = dot(pos, 1.0 / (ab * ab));

	ab -= strokeWidth / 2.0;
    float innerDist = dot(pos, 1.0 / (ab * ab));
    float outerDelta = fwidth(outerDist) * 0.8;
    float innerDelta = fwidth(innerDist) * 0.8;

    float innerAlpha = smoothstep(1.0 - innerDelta, 1.0 + innerDelta, innerDist);
    float outerAlpha = smoothstep(1.0 - outerDelta, 1.0 + outerDelta, outerDist);

    vec4 sc = strokeColor * 1.0;//step(innerDelta, strokeWidth);

    fragColor = mix(sc, vec4(1, 1, 1, 1), outerAlpha);
	fragColor = mix(fillColor, fragColor, innerAlpha);
}
