void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 q = uv - vec2(0.5,0.5);

    vec3 col = vec3(1.0,1.0,1.0);

    float s = 0.005* (0.5+sin(iTime*2.0+(uv.x*25.0)*0.5)/2.0)*10.5;
    float r = 0.19;
    col *= 1.0-smoothstep( r, r+s,  length (q) );
    float w = 0.17;
    col *= 1.0-smoothstep( w, w+s, abs(0.5-uv.x));
	float h = 0.1;
    col *= 1.0-smoothstep( h, h+s, abs(0.5-uv.y));
    vec3 col_Inv= 1.0-col;
    col *= smoothstep( 0.2, 0.67, uv.y) * vec3(0.4,1.0,0.3);
    col = col+ col_Inv;



	fragColor = vec4(col,1.0);
}
