renderpass.code:
vec2 A = vec2(0.5,0.8);
vec2 B = vec2(0.3,0.25);
vec2 C = vec2(0.7,0.25);

vec2 vecC = C - A;
vec2 vecB = B - A;

float dotCC = dot(vecC,vecC);
float dotCB = dot(vecC,vecB);
float dotBB = dot(vecB,vecB);

float invDenom = 1.0 / (dotCC * dotBB - dotCB * dotCB);

bool insideTriangle(vec2 P)
{
    vec2 vecP = P - A;

    float dotCP = dot(vecC,vecP);
    float dotBP = dot(vecB,vecP);

    float u = (dotBB * dotCP - dotCB * dotBP) * invDenom;
    float v = (dotCC * dotBP - dotCB * dotCP) * invDenom;

    return (u >= 0.0) && (v >= 0.0) && (u + v < 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    if ( insideTriangle(uv) )
    {
        fragColor = texture(iChannel0, uv);
    }
    else
    {
     	fragColor = vec4(0,0,0,1);
    }
}
