#define TIMECONST (iTime*50.0)
#define TIMECONST2 (iTime*.5)
#define SPLITCONST 16.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord.xy / iResolution.xy*SPLITCONST;

    float xVal,yVal;
    if(int(uv.x)%2==0)
        xVal=sin(fragCoord.x+TIMECONST);//+cos(TIMECONST2);
    else
        xVal=sin(fragCoord.x-TIMECONST)+cos(TIMECONST2);

    if(int(uv.y)%2==0)
        yVal=sin(fragCoord.y+TIMECONST);//+cos(TIMECONST2);
    else
        yVal=sin(fragCoord.y-TIMECONST)+cos(TIMECONST2);

    fragColor=vec4(xVal,yVal,xVal+yVal+sin(iTime),1);
}
