#define BARCOUNT_INT 32
#define BARCOUNT 32.0


vec3 visualizerBar(sampler2D audio, vec2 uv, int barIndex)
{
    float intensity = texture(iChannel0, vec2(float(barIndex)/BARCOUNT , 0.1)).x;
    if(uv.y > 0.0 && uv.y < (abs(intensity) - abs((uv.x) - (float(barIndex) + sin(iTime)*sin(iTime))/BARCOUNT ) ))
        return mix(vec3(0.0,0.0,0.0), vec3(1.0-uv.x, 0.1, 0.6 + (uv.x - (float(barIndex)/BARCOUNT ))/(1.0/BARCOUNT )*0.4), 0.25+0.5*uv.y);
    return vec3(uv.y-abs(intensity), 0.0, uv.y-abs(intensity)*0.4);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(uv,0.9+0.1*sin(iTime),0.6+0.4*sin(iTime*0.67));
    vec4 outputC = vec4(0.0,0.0,0.0,0.0);
    for(int i = 0; i < BARCOUNT_INT; i++)
    {
        if(uv.x < ((float(i)+2.0)/BARCOUNT ) && uv.x > (float(i)/BARCOUNT ))
            outputC = mix(fragColor, vec4(visualizerBar(iChannel0, uv, i), 1.0), 1.0-(0.4*uv.x));
    }
    fragColor = mix(fragColor, outputC, 0.5);
    fragColor = mix(fragColor, vec4(0.0, 0.0, 0.0, 0.0), 0.2*texture(iChannel0, (vec2(0.35, 0.1)+vec2(0.15, 0.1))/2.0).x);
}
